import React, { Component } from 'react';

export default class ToggleMore extends Component {
    state = {
        on: false,
    }

    toggle = () => {
        this.setState({
            on: !this.state.on
        });
    };
    render() {
        return (
            <div className="tempExpand">
                <button onClick={this.toggle}>
                    Show More..
                </button>

                {this.state.on && <h1>hey</h1>}

            </div>
        )
    }
}
